"""##Métricas Baseadas em Distâncias

Distribuição das Distâncias Mínimas
"""

[k for k in nx.shortest_path_length(Op)]

d_ij = [k for k in nx.shortest_path_length(Op)]

distancias = []

for _, d in d_ij:
  tmp = [v for v in d.values() if v > 0]
  distancias.extend(tmp)

fig = plt.figure(1, figsize=(4,3))

ax_hist = plt.axes([0, .2, 1, .8])
ax_box = plt.axes([0, .0, 1, .2])

ax_hist.hist(distancias, align='mid', bins=range(0,8))
ax_hist.set_title("Distribuição das Distâncias")
ax_hist.set_ylabel("Quantidade")
ax_box.set_xlabel("Distância")
ax_box.boxplot(distancias, widths = .5, vert=False)
ax_box.set_xlim([0,7])
plt.show()

