"""###Quantidade comunidades"""

from networkx.algorithms.community.modularity_max import greedy_modularity_communities

c = list(greedy_modularity_communities(Op))
print(len(c))

"""###Possui 5 comunidades"""

for k in c:
  print(k)

node_community = {}
for idx, community in enumerate(c):
    for node in community:
        node_community[node] = idx

colors = ['red', 'blue', 'green', 'orange', 'purple', 'cyan']

node_colors = [colors[node_community[node]] for node in Op.nodes()]

plt.figure(figsize=(10, 8))
pos = nx.spring_layout(Op)
nx.draw(Op, pos, node_color=node_colors, with_labels=True)
plt.show()

